module.exports = function cors(options = {}) {
  const origin = options.origin || '*';
  const allowMethods = options.methods || 'GET,HEAD,PUT,PATCH,POST,DELETE';
  const allowHeaders = options.allowedHeaders || 'Origin, X-Requested-With, Content-Type, Accept, Authorization';
  const credentials = options.credentials || false;
  return function(req, res, next) {
    res.setHeader('Access-Control-Allow-Origin', origin);
    res.setHeader('Access-Control-Allow-Methods', allowMethods);
    res.setHeader('Access-Control-Allow-Headers', allowHeaders);
    if (credentials) {
      res.setHeader('Access-Control-Allow-Credentials', 'true');
    }
    if (req.method === 'OPTIONS') {
      res.statusCode = 204;
      return res.end();
    }
    next();
  };
};
